import { Card, Button } from 'react-bootstrap'; 
import { useState, useEffect } from 'react'; 
import propTypes from 'prop-types'

const SimpleCard = ({title, text, img, link}) => {
  const [url, setUrl] = useState('https://res.cloudinary.com/draketech/image/upload/c_scale,e_blur:2500,h_300/v1593458916/RegoRealty/agents/Cliff_sxs5kn.jpg'); 

  const imageAppend = () => {
    let position = link.indexOf('upload/'); 
    position += 7; 
    console.log(position); 
    let imageUrl = link.substring(0, position) + 'h_300/' + link.substring(position);
    setUrl(imageUrl); 
  }; 

  useEffect(() => {
    imageAppend(); 
  }); 





    return (





<a href={link} className='card-hover'>  
<Card className='simple-card'>
  <Card.Img variant="top" src={img} style={{borderRadius: '15px 15px 0 0', opacity: '0.5' }} />
  <Card.Body>
    <Card.Title> {title} </Card.Title>
    <Card.Text>
      {text}
    </Card.Text>
  </Card.Body>
</Card>
</a>    

    ); 

}
SimpleCard.propTypes = {
    img: String, 
    title: String, 
    text: String, 
    link: String, 
}

SimpleCard.defaultProps = {
    img: 'https://res.cloudinary.com/ddd2lfnyh/image/upload/v1605294214/Screen_Shot_2020-11-13_at_1.21.54_PM_ywyjyl.png', 
    title: 'Rego Realty', 
    text: 'Some quick example text to build on the card title and make up the bulk of the content.',
    link: '', 
}





export default SimpleCard;
